import json
import boto3
import os
import time
from datetime import datetime

mediaconvert_client = None
dynamodb = boto3.resource('dynamodb')
sns = boto3.client('sns')

def get_mediaconvert_client():
    global mediaconvert_client
    if mediaconvert_client is None:
        client = boto3.client('mediaconvert', region_name=os.environ.get('AWS_REGION', 'ap-southeast-1'))
        endpoints = client.describe_endpoints()
        mediaconvert_client = boto3.client('mediaconvert', endpoint_url=endpoints['Endpoints'][0]['Url'])
    return mediaconvert_client

def lambda_handler(event, context):
    print(f"Received event: {json.dumps(event)}")

    # Extract bucket and key from event (supports EventBridge and direct invocation)
    bucket = None
    key = None

    # Handle EventBridge event pattern
    if 'detail' in event and 'bucket' in event['detail']:
        bucket = event['detail']['bucket']['name']
        key = event['detail']['object']['key']
    # Handle direct invocation from Step Functions
    elif 'bucket' in event and 'key' in event:
        bucket = event['bucket']
        key = event['key']

    if not bucket or not key:
        return {
            'statusCode': 400,
            'body': json.dumps('Invalid event: missing bucket or key')
        }

    job_id = f"job-{int(time.time())}"

    # Store job status in DynamoDB
    table = dynamodb.Table(os.environ['JOB_STATUS_TABLE'])
    table.put_item(
        Item={
            'jobId': job_id,
            'status': 'SUBMITTED',
            'inputBucket': bucket,
            'inputKey': key,
            'timestamp': int(time.time()),
            'createdAt': datetime.utcnow().isoformat()
        }
    )

    # Create MediaConvert job
    mc_client = get_mediaconvert_client()

    job_settings = {
        'Role': os.environ['MEDIACONVERT_ROLE_ARN'],
        'Settings': {
            'Inputs': [
                {
                    'FileInput': f's3://{bucket}/{key}',
                    'AudioSelectors': {
                        'Audio Selector 1': {
                            'DefaultSelection': 'DEFAULT'
                        }
                    },
                    'VideoSelector': {}
                }
            ],
            'OutputGroups': [
                {
                    'Name': 'HLS',
                    'OutputGroupSettings': {
                        'Type': 'HLS_GROUP_SETTINGS',
                        'HlsGroupSettings': {
                            'Destination': f's3://{os.environ["OUTPUT_BUCKET"]}/{key.split(".")[0]}/hls/',
                            'SegmentLength': 10
                        }
                    },
                    'Outputs': [
                        {
                            'NameModifier': '_1080p',
                            'VideoDescription': {
                                'Width': 1920,
                                'Height': 1080,
                                'CodecSettings': {
                                    'Codec': 'H_264',
                                    'H264Settings': {
                                        'Bitrate': 5000000
                                    }
                                }
                            },
                            'AudioDescriptions': [
                                {
                                    'CodecSettings': {
                                        'Codec': 'AAC',
                                        'AacSettings': {
                                            'Bitrate': 128000
                                        }
                                    }
                                }
                            ]
                        },
                        {
                            'NameModifier': '_720p',
                            'VideoDescription': {
                                'Width': 1280,
                                'Height': 720,
                                'CodecSettings': {
                                    'Codec': 'H_264',
                                    'H264Settings': {
                                        'Bitrate': 3000000
                                    }
                                }
                            },
                            'AudioDescriptions': [
                                {
                                    'CodecSettings': {
                                        'Codec': 'AAC',
                                        'AacSettings': {
                                            'Bitrate': 128000
                                        }
                                    }
                                }
                            ]
                        },
                        {
                            'NameModifier': '_480p',
                            'VideoDescription': {
                                'Width': 854,
                                'Height': 480,
                                'CodecSettings': {
                                    'Codec': 'H_264',
                                    'H264Settings': {
                                        'Bitrate': 1500000
                                    }
                                }
                            },
                            'AudioDescriptions': [
                                {
                                    'CodecSettings': {
                                        'Codec': 'AAC',
                                        'AacSettings': {
                                            'Bitrate': 96000
                                        }
                                    }
                                }
                            ]
                        },
                        {
                            'NameModifier': '_360p',
                            'VideoDescription': {
                                'Width': 640,
                                'Height': 360,
                                'CodecSettings': {
                                    'Codec': 'H_264',
                                    'H264Settings': {
                                        'Bitrate': 800000
                                    }
                                }
                            },
                            'AudioDescriptions': [
                                {
                                    'CodecSettings': {
                                        'Codec': 'AAC',
                                        'AacSettings': {
                                            'Bitrate': 96000
                                        }
                                    }
                                }
                            ]
                        }
                    ]
                }
            ]
        }
    }

    try:
        response = mc_client.create_job(**job_settings)
        mc_job_id = response['Job']['Id']

        # Update DynamoDB with MediaConvert job ID
        table.update_item(
            Key={'jobId': job_id},
            UpdateExpression='SET #status = :status, mediaConvertJobId = :mc_job_id',
            ExpressionAttributeNames={'#status': 'status'},
            ExpressionAttributeValues={
                ':status': 'PROCESSING',
                ':mc_job_id': mc_job_id
            }
        )

        # Send SNS notification
        sns.publish(
            TopicArn=os.environ['SNS_TOPIC_ARN'],
            Message=json.dumps({
                'status': 'PROCESSING',
                'jobId': job_id,
                'mediaConvertJobId': mc_job_id
            }),
            Subject='Video Transcoding Job Started'
        )

        return {
            'statusCode': 200,
            'body': json.dumps({
                'jobId': job_id,
                'mediaConvertJobId': mc_job_id,
                'status': 'PROCESSING'
            })
        }
    except Exception as e:
        print(f"Error creating MediaConvert job: {str(e)}")
        table.update_item(
            Key={'jobId': job_id},
            UpdateExpression='SET #status = :status, errorMessage = :error',
            ExpressionAttributeNames={'#status': 'status'},
            ExpressionAttributeValues={
                ':status': 'FAILED',
                ':error': str(e)
            }
        )
        # Send failure notification
        sns.publish(
            TopicArn=os.environ['SNS_TOPIC_ARN'],
            Message=json.dumps({
                'status': 'FAILED',
                'jobId': job_id,
                'error': str(e)
            }),
            Subject='Video Transcoding Job Failed'
        )
        raise

