{
  "Comment": "Video transcoding workflow with retry logic",
  "StartAt": "StartTranscoding",
  "States": {
    "StartTranscoding": {
      "Type": "Task",
      "Resource": "${TranscodingOrchestratorFunction.Arn}",
      "Retry": [
        {
          "ErrorEquals": ["States.ALL"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "Next": "TranscodingFailed"
        }
      ],
      "Next": "WaitForCompletion"
    },
    "WaitForCompletion": {
      "Type": "Wait",
      "Seconds": 60,
      "Next": "CheckJobStatus"
    },
    "CheckJobStatus": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.statusCode",
          "NumericEquals": 200,
          "Next": "TranscodingSuccess"
        }
      ],
      "Default": "TranscodingFailed"
    },
    "TranscodingSuccess": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "${TranscodingCompleteTopic}",
        "Message": {
          "status": "SUCCESS",
          "jobId.$": "$.body"
        }
      },
      "End": true
    },
    "TranscodingFailed": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sns:publish",
      "Parameters": {
        "TopicArn": "${TranscodingCompleteTopic}",
        "Message": {
          "status": "FAILED",
          "error.$": "$.Error"
        }
      },
      "End": true
    }
  }
}
