Fn::Sub:
#!/bin/bash
set -e
yum update -y
yum install -y amazon-cloudwatch-agent

# Install application dependencies
yum install -y httpd mysql

# Configure CloudWatch agent
cat > /opt/aws/amazon-cloudwatch-agent/etc/config.json << 'EOF'
{
  "logs": {
    "logs_collected": {
      "files": {
        "collect_list": [
          {
            "file_path": "/var/log/httpd/access_log",
            "log_group_name": "/aws/ec2/${EnvironmentSuffix}/httpd",
            "log_stream_name": "{instance_id}/access.log"
          },
          {
            "file_path": "/var/log/httpd/error_log",
            "log_group_name": "/aws/ec2/${EnvironmentSuffix}/httpd",
            "log_stream_name": "{instance_id}/error.log"
          }
        ]
      }
    }
  },
  "metrics": {
    "namespace": "FinancialApp/${EnvironmentSuffix}",
    "metrics_collected": {
      "mem": {
        "measurement": [
          {"name": "mem_used_percent", "rename": "MemoryUsedPercent", "unit": "Percent"}
        ]
      },
      "disk": {
        "measurement": [
          {"name": "used_percent", "rename": "DiskUsedPercent", "unit": "Percent"}
        ],
        "resources": ["*"]
      }
    }
  }
}
EOF

# Start CloudWatch agent
/opt/aws/amazon-cloudwatch-agent/bin/amazon-cloudwatch-agent-ctl \
  -a fetch-config \
  -m ec2 \
  -s \
  -c file:/opt/aws/amazon-cloudwatch-agent/etc/config.json

# Configure application
cat > /var/www/html/index.html << 'EOF'
<!DOCTYPE html>
<html>
<head>
  <title>Financial Services Application</title>
</head>
<body>
  <h1>Financial Services Transaction Processing</h1>
  <p>Environment: ${EnvironmentSuffix}</p>
  <p>Instance ID: $(ec2-metadata --instance-id | cut -d " " -f 2)</p>
  <p>Availability Zone: $(ec2-metadata --availability-zone | cut -d " " -f 2)</p>
</body>
</html>
EOF

# Health check endpoint
cat > /var/www/html/health << 'EOF'
OK
EOF

# Start httpd
systemctl enable httpd
systemctl start httpd

echo "Instance configuration completed successfully"
