import json
import boto3

config_client = boto3.client('config')
ec2_client = boto3.client('ec2')
rds_client = boto3.client('rds')
s3_client = boto3.client('s3')

def lambda_handler(event, context):
    print(f"Received event: {json.dumps(event)}")

    invoking_event = json.loads(event['invokingEvent'])
    configuration_item = invoking_event['configurationItem']

    resource_type = configuration_item['resourceType']
    resource_id = configuration_item['resourceId']

    compliance_type = 'COMPLIANT'
    annotation = 'Resource is encrypted'

    try:
        if resource_type == 'AWS::RDS::DBInstance':
            response = rds_client.describe_db_instances(DBInstanceIdentifier=resource_id)
            encrypted = response['DBInstances'][0].get('StorageEncrypted', False)
            if not encrypted:
                compliance_type = 'NON_COMPLIANT'
                annotation = 'RDS instance is not encrypted'

        elif resource_type == 'AWS::S3::Bucket':
            try:
                response = s3_client.get_bucket_encryption(Bucket=resource_id)
                if not response.get('ServerSideEncryptionConfiguration'):
                    compliance_type = 'NON_COMPLIANT'
                    annotation = 'S3 bucket does not have encryption enabled'
            except s3_client.exceptions.ServerSideEncryptionConfigurationNotFoundError:
                compliance_type = 'NON_COMPLIANT'
                annotation = 'S3 bucket does not have encryption configured'

        elif resource_type == 'AWS::EC2::Volume':
            response = ec2_client.describe_volumes(VolumeIds=[resource_id])
            encrypted = response['Volumes'][0].get('Encrypted', False)
            if not encrypted:
                compliance_type = 'NON_COMPLIANT'
                annotation = 'EBS volume is not encrypted'

        else:
            compliance_type = 'NOT_APPLICABLE'
            annotation = 'Resource type not evaluated for encryption'

    except Exception as e:
        print(f"Error evaluating resource: {str(e)}")
        compliance_type = 'NOT_APPLICABLE'
        annotation = f'Error evaluating resource: {str(e)}'

    evaluations = [{
        'ComplianceResourceType': resource_type,
        'ComplianceResourceId': resource_id,
        'ComplianceType': compliance_type,
        'Annotation': annotation,
        'OrderingTimestamp': configuration_item['configurationItemCaptureTime']
    }]

    result_token = event.get('resultToken', 'No token found')

    if result_token != 'No token found':
        config_client.put_evaluations(
            Evaluations=evaluations,
            ResultToken=result_token
        )

    print(f"Evaluation result: {compliance_type} - {annotation}")

    return {
        'statusCode': 200,
        'body': json.dumps({
            'compliance': compliance_type,
            'annotation': annotation
        })
    }

import json
import boto3

config_client = boto3.client('config')
ec2_client = boto3.client('ec2')

RESTRICTED_PORTS = [22, 3389]

def lambda_handler(event, context):
    print(f"Received event: {json.dumps(event)}")

    invoking_event = json.loads(event['invokingEvent'])
    configuration_item = invoking_event['configurationItem']

    resource_type = configuration_item['resourceType']
    resource_id = configuration_item['resourceId']

    if resource_type != 'AWS::EC2::SecurityGroup':
        return {'statusCode': 200, 'body': 'Not a security group'}

    compliance_type = 'COMPLIANT'
    annotation = 'Security group rules are compliant'
    violations = []

    try:
        response = ec2_client.describe_security_groups(GroupIds=[resource_id])
        security_group = response['SecurityGroups'][0]

        for rule in security_group.get('IpPermissions', []):
            from_port = rule.get('FromPort', 0)
            to_port = rule.get('ToPort', 0)

            for ip_range in rule.get('IpRanges', []):
                cidr = ip_range.get('CidrIp', '')
                if cidr == '0.0.0.0/0':
                    for port in RESTRICTED_PORTS:
                        if from_port <= port <= to_port:
                            violations.append(f"Port {port} open to 0.0.0.0/0")

            for ipv6_range in rule.get('Ipv6Ranges', []):
                cidr = ipv6_range.get('CidrIpv6', '')
                if cidr == '::/0':
                    for port in RESTRICTED_PORTS:
                        if from_port <= port <= to_port:
                            violations.append(f"Port {port} open to ::/0")

        if violations:
            compliance_type = 'NON_COMPLIANT'
            annotation = f"Security violations: {'; '.join(violations)}"

    except Exception as e:
        print(f"Error evaluating security group: {str(e)}")
        compliance_type = 'NOT_APPLICABLE'
        annotation = f'Error evaluating security group: {str(e)}'

    evaluations = [{
        'ComplianceResourceType': resource_type,
        'ComplianceResourceId': resource_id,
        'ComplianceType': compliance_type,
        'Annotation': annotation,
        'OrderingTimestamp': configuration_item['configurationItemCaptureTime']
    }]

    result_token = event.get('resultToken', 'No token found')

    if result_token != 'No token found':
        config_client.put_evaluations(
            Evaluations=evaluations,
            ResultToken=result_token
        )

    print(f"Evaluation result: {compliance_type} - {annotation}")

    return {
        'statusCode': 200,
        'body': json.dumps({
            'compliance': compliance_type,
            'annotation': annotation,
            'violations': violations
        })
    }

import json
import boto3
import os
from datetime import datetime

s3_client = boto3.client('s3')
config_client = boto3.client('config')
sns_client = boto3.client('sns')

def lambda_handler(event, context):
    print(f"Generating compliance report")

    reports_bucket = os.environ['REPORTS_BUCKET']
    sns_topic_arn = os.environ['SNS_TOPIC_ARN']

    try:
        rules_response = config_client.describe_config_rules()
        config_rules = rules_response.get('ConfigRules', [])

        report = {
            'timestamp': datetime.utcnow().isoformat(),
            'rules': [],
            'summary': {
                'total_rules': 0,
                'compliant': 0,
                'non_compliant': 0,
                'not_applicable': 0
            }
        }

        critical_violations = []

        for rule in config_rules:
            rule_name = rule['ConfigRuleName']

            try:
                compliance_response = config_client.get_compliance_details_by_config_rule(
                    ConfigRuleName=rule_name,
                    ComplianceTypes=['NON_COMPLIANT'],
                    Limit=100
                )

                non_compliant_resources = compliance_response.get('EvaluationResults', [])

                rule_report = {
                    'rule_name': rule_name,
                    'non_compliant_count': len(non_compliant_resources),
                    'violations': []
                }

                for resource in non_compliant_resources:
                    violation = {
                        'resource_type': resource['EvaluationResultIdentifier']['EvaluationResultQualifier']['ResourceType'],
                        'resource_id': resource['EvaluationResultIdentifier']['EvaluationResultQualifier']['ResourceId'],
                        'annotation': resource.get('Annotation', 'No annotation')
                    }
                    rule_report['violations'].append(violation)
                    critical_violations.append(violation)

                report['rules'].append(rule_report)
                report['summary']['total_rules'] += 1

                if len(non_compliant_resources) > 0:
                    report['summary']['non_compliant'] += 1
                else:
                    report['summary']['compliant'] += 1

            except Exception as e:
                print(f"Error evaluating rule {rule_name}: {str(e)}")

        report_key = f"compliance-reports/{datetime.utcnow().strftime('%Y/%m/%d')}/report-{datetime.utcnow().strftime('%Y%m%d-%H%M%S')}.json"

        s3_client.put_object(
            Bucket=reports_bucket,
            Key=report_key,
            Body=json.dumps(report, indent=2),
            ContentType='application/json'
        )

        print(f"Report saved to s3://{reports_bucket}/{report_key}")

        if critical_violations:
            message = f"AWS Config Compliance Report\n\n"
            message += f"Timestamp: {report['timestamp']}\n"
            message += f"Total Rules: {report['summary']['total_rules']}\n"
            message += f"Non-Compliant: {report['summary']['non_compliant']}\n"
            message += f"Compliant: {report['summary']['compliant']}\n\n"
            message += f"Critical Violations: {len(critical_violations)}\n\n"
            message += f"Report Location: s3://{reports_bucket}/{report_key}"

            sns_client.publish(
                TopicArn=sns_topic_arn,
                Subject='AWS Config Compliance Report - Action Required',
                Message=message
            )

            print(f"Notification sent to SNS topic: {sns_topic_arn}")

        return {
            'statusCode': 200,
            'body': json.dumps({
                'report_location': f"s3://{reports_bucket}/{report_key}",
                'summary': report['summary']
            })
        }

    except Exception as e:
        print(f"Error generating report: {str(e)}")
        return {
            'statusCode': 500,
            'body': json.dumps({'error': str(e)})
        }

