import boto3

def add_tags(events, context):
    resource_id = events['ResourceId']
    resource_type = events['ResourceType']

    default_tags = [
        {'Key': 'Environment', 'Value': 'production'},
        {'Key': 'Owner', 'Value': 'compliance-team'},
        {'Key': 'CostCenter', 'Value': 'security'}
    ]

    if resource_type == 'AWS::EC2::Instance':
        ec2 = boto3.client('ec2')
        ec2.create_tags(Resources=[resource_id], Tags=default_tags)
    elif resource_type == 'AWS::S3::Bucket':
        s3 = boto3.client('s3')
        tag_set = [{'Key': tag['Key'], 'Value': tag['Value']} for tag in default_tags]
        s3.put_bucket_tagging(Bucket=resource_id, Tagging={'TagSet': tag_set})

    return {'message': 'Tags added successfully'}
