import json
import boto3
import os
import time
from datetime import datetime, timedelta
from decimal import Decimal

# Use the region where the Lambda function is deployed
aws_region = os.environ.get('AWS_REGION', 'us-east-1')

sns = boto3.client('sns', region_name=aws_region)
dynamodb = boto3.resource('dynamodb', region_name=aws_region)
ses = boto3.client('ses', region_name=aws_region)
cloudwatch = boto3.client('cloudwatch', region_name=aws_region)

def lambda_handler(event, context):
    table = dynamodb.Table(os.environ['TABLE_NAME'])
    results = {'successful': 0, 'failed': 0, 'fallback': 0}

    appointments = event.get('appointments', [])

    for appointment in appointments:
        success = send_notification(appointment, table)
        if success:
            results['successful'] += 1
        else:
            results['failed'] += 1

    send_metrics(results)

    return {
        'statusCode': 200,
        'body': json.dumps(results)
    }

def send_notification(appointment, table):
    patient_id = appointment.get('patient_id')
    phone = appointment.get('phone_number')
    message = appointment.get('message')

    for attempt in range(3):
        try:
            response = sns.publish(
                PhoneNumber=phone,
                Message=message,
                MessageAttributes={
                    'AWS.SNS.SMS.SMSType': {
                        'DataType': 'String',
                        'StringValue': 'Transactional'
                    }
                }
            )

            log_delivery(table, patient_id, phone, message, 'SUCCESS', attempt + 1)
            return True

        except Exception as e:
            print(f'SMS attempt {attempt + 1} failed: {str(e)}')

            if attempt == 2:
                send_email_fallback(appointment)
                log_delivery(table, patient_id, phone, message, 'FAILED_WITH_FALLBACK', attempt + 1)
                return False

            time.sleep(2 ** attempt)

    return False

def log_delivery(table, patient_id, phone, message, status, retry_count):
    timestamp = int(datetime.now().timestamp())
    ttl = int((datetime.now() + timedelta(days=90)).timestamp())

    table.put_item(
        Item={
            'patientId': patient_id,
            'timestamp': timestamp,
            'phoneNumber': phone,
            'messageContent': message,
            'deliveryStatus': status,
            'retryCount': retry_count,
            'ttl': ttl
        }
    )

def send_email_fallback(appointment):
    try:
        ses.send_email(
            Source=os.environ['SENDER_EMAIL'],
            Destination={'ToAddresses': [appointment.get('email', '')]},
            Message={
                'Subject': {'Data': 'Appointment Reminder'},
                'Body': {'Text': {'Data': appointment.get('message', '')}}
            }
        )
    except Exception as e:
        print(f'Email fallback failed: {str(e)}')

def send_metrics(results):
    cloudwatch.put_metric_data(
        Namespace='AppointmentReminders',
        MetricData=[
            {
                'MetricName': 'SuccessfulSMS',
                'Value': results['successful'],
                'Unit': 'Count'
            },
            {
                'MetricName': 'FailedSMS',
                'Value': results['failed'],
                'Unit': 'Count'
            }
        ]
    )
