import boto3
import json
import os
from datetime import datetime, timedelta, timezone

def lambda_handler(event, context):
    ec2 = boto3.client('ec2')
    retention_hours = int(os.environ['RETENTION_HOURS'])
    require_tag = os.environ['REQUIRE_TAG'].lower() == 'true'

    cutoff_time = datetime.now(timezone.utc) - timedelta(hours=retention_hours)
    deleted_volumes = []

    paginator = ec2.get_paginator('describe_volumes')
    page_iterator = paginator.paginate(Filters=[{'Name': 'status', 'Values': ['available']}])

    for page in page_iterator:
        for volume in page.get('Volumes', []):
            volume_id = volume['VolumeId']
            create_time = volume['CreateTime']  # UTC

            if create_time > cutoff_time:
                continue

            if require_tag:
                tags = {t['Key']: t['Value'] for t in volume.get('Tags', [])}
                if tags.get('AutoDelete', '').lower() != 'true':
                    continue

            try:
                ec2.delete_volume(VolumeId=volume_id)
                deleted_volumes.append(volume_id)
                print(f"Deleted volume: {volume_id}")
            except Exception as e:
                print(f"Failed to delete volume {volume_id}: {str(e)}")

    return {'statusCode': 200, 'body': json.dumps({'deleted_volumes': deleted_volumes, 'count': len(deleted_volumes)})}
