import json
import time
import boto3
import traceback
import urllib3

http = urllib3.PoolManager()

def send_response(event, context, status, data):
    body = json.dumps({
        'Status': status,
        'Reason': f'See log stream: {context.log_stream_name}',
        'PhysicalResourceId': context.log_stream_name,
        'StackId': event.get('StackId'),
        'RequestId': event.get('RequestId'),
        'LogicalResourceId': event.get('LogicalResourceId'),
        'Data': data
    })
    headers = {
        'content-type': '',
        'content-length': str(len(body))
    }
    try:
        http.request('PUT', event['ResponseURL'], body=body, headers=headers)
    except Exception as e:
        print("Failed to send response:", e)

def handler(event, context):
    print("Event:", event)
    recorder = event['ResourceProperties'].get('RecorderName')
    response_data = {}
    try:
        if event['RequestType'] in ('Create', 'Update'):
            config = boto3.client('config')
            # retry loop
            max_retries = 5
            delay = 5
            for i in range(max_retries):
                try:
                    print(f"Attempt {i+1} to start recorder {recorder}")
                    config.start_configuration_recorder(ConfigurationRecorderName=recorder)
                    response_data['Started'] = True
                    print("Recorder started successfully")
                    break
                except config.exceptions.NoAvailableDeliveryChannelException as e:
                    print("Delivery channel not yet available, retrying...", e)
                    time.sleep(delay)
                except Exception as e:
                    print("Other exception:", e, traceback.format_exc())
                    raise
            else:
                # after retries
                raise Exception("Could not start configuration recorder after retries")
        elif event['RequestType'] == 'Delete':
            # nothing to do on delete
            response_data['Deleted'] = True

        send_response(event, context, 'SUCCESS', response_data)
    except Exception as e:
        print("Error in handler:", e, traceback.format_exc())
        send_response(event, context, 'FAILED', {'Error': str(e)})
