import json
import os
import boto3
import re
import logging
import sys
import uuid
from boto3.dynamodb.conditions import Key

# Configure structured logging
logger = logging.getLogger()
logger.setLevel(getattr(logging, os.environ.get('LOG_LEVEL', 'INFO')))

# Remove default handlers and add custom formatter
for handler in logger.handlers:
    logger.removeHandler(handler)

handler = logging.StreamHandler(sys.stdout)
formatter = logging.Formatter(
    '{"timestamp": "%(asctime)s", "level": "%(levelname)s", "message": "%(message)s", "correlation_id": "%(correlation_id)s"}'
)
handler.setFormatter(formatter)
logger.addHandler(handler)

def validate_user_id(user_id):
    """Validate user ID format (UUID)"""
    if not user_id or not isinstance(user_id, str):
        return False

    # Check if it's a valid UUID format
    uuid_pattern = r'^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$'
    return bool(re.match(uuid_pattern, user_id.lower()))

def lambda_handler(event, context):
    # Generate correlation ID for request tracing
    correlation_id = str(uuid.uuid4())

    # Add correlation ID to logger context
    old_factory = logging.getLogRecordFactory()
    def record_factory(*args, **kwargs):
        record = old_factory(*args, **kwargs)
        record.correlation_id = correlation_id
        return record
    logging.setLogRecordFactory(record_factory)

    logger.info(f"GetUser request started", extra={'correlation_id': correlation_id})

    # Feature flag check
    if os.environ.get('FEATURE_FLAG_CACHING', 'false').lower() == 'true':
        logger.info("Feature flag caching is enabled", extra={'correlation_id': correlation_id})

    cors_origin = '*'
    if os.environ.get('ENVIRONMENT') == 'prod':
        cors_origin = 'https://yourdomain.com'

    try:
        # Extract and validate user ID from path parameters
        path_params = event.get('pathParameters') or {}
        user_id = path_params.get('id')

        logger.info(f"Processing get user request for ID: {user_id}", extra={'correlation_id': correlation_id})

        if not validate_user_id(user_id):
            logger.warning(f"Invalid user ID format: {user_id}", extra={'correlation_id': correlation_id})
            return {
                'statusCode': 400,
                'headers': {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': cors_origin,
                    'X-Correlation-ID': correlation_id
                },
                'body': json.dumps({'error': 'Invalid user ID format. Must be a valid UUID.'})
            }

        # Get user from DynamoDB
        dynamodb = boto3.resource('dynamodb')
        table = dynamodb.Table(os.environ['DYNAMODB_TABLE'])

        response = table.get_item(Key={'id': user_id})

        if 'Item' in response:
            logger.info(f"User found: {user_id}", extra={'correlation_id': correlation_id})
            return {
                'statusCode': 200,
                'headers': {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': cors_origin,
                    'Access-Control-Allow-Headers': 'Content-Type,X-Amz-Date,Authorization,X-Api-Key,X-Amz-Security-Token',
                    'Access-Control-Allow-Methods': 'GET,OPTIONS',
                    'X-Correlation-ID': correlation_id
                },
                'body': json.dumps(response['Item'], default=str)
            }
        else:
            logger.warning(f"User not found: {user_id}", extra={'correlation_id': correlation_id})
            return {
                'statusCode': 404,
                'headers': {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': cors_origin,
                    'X-Correlation-ID': correlation_id
                },
                'body': json.dumps({'error': 'User not found'})
            }
    except Exception as e:
        logger.error(f"Unexpected error: {str(e)}", extra={'correlation_id': correlation_id})
        return {
            'statusCode': 500,
            'headers': {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': cors_origin,
                'X-Correlation-ID': correlation_id
            },
            'body': json.dumps({'error': 'Internal server error'})
        }
