const { DynamoDBClient, PutItemCommand, GetItemCommand } = require('@aws-sdk/client-dynamodb');
const { marshall, unmarshall } = require('@aws-sdk/util-dynamodb');
const ddbClient = new DynamoDBClient({ region: process.env.AWS_REGION });

exports.handler = async (event) => {
  console.log('Received event:', JSON.stringify(event, null, 2));
  const ordersTableName = process.env.ORDERS_TABLE_NAME;

  const headers = {
    'Content-Type': 'application/json',
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Methods': 'GET, POST, OPTIONS',
    'Access-Control-Allow-Headers': 'Content-Type'
  };

  try {
    switch (event.httpMethod) {
      case 'POST':
        const order = JSON.parse(event.body);
        // Auto-generate orderId if not provided
        if (!order.orderId) {
          order.orderId = `order-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
        }
        order.createdAt = new Date().toISOString();
        order.status = order.status || 'PENDING';

        const putParams = {
          TableName: ordersTableName,
          Item: marshall(order)
        };
        await ddbClient.send(new PutItemCommand(putParams));
        return {
          statusCode: 201,
          headers,
          body: JSON.stringify({ 
            message: 'Order created successfully',
            orderId: order.orderId,
            status: order.status
          })
        };

      case 'GET':
        const orderId = event.queryStringParameters?.orderId;
        if (!orderId) {
          return {
            statusCode: 400,
            headers,
            body: JSON.stringify({ message: 'Missing orderId query parameter' })
          };
        }
        const getParams = {
          TableName: ordersTableName,
          Key: marshall({ orderId })
        };
        const { Item } = await ddbClient.send(new GetItemCommand(getParams));
        if (!Item) {
          return {
            statusCode: 404,
            headers,
            body: JSON.stringify({ message: 'Order not found' })
          };
        }
        return {
          statusCode: 200,
          headers,
          body: JSON.stringify(unmarshall(Item))
        };

      default:
        return {
          statusCode: 405,
          headers,
          body: JSON.stringify({ message: 'Method Not Allowed' })
        };
    }
  } catch (error) {
    console.error('Error:', error);
    return {
      statusCode: 500,
      headers,
      body: JSON.stringify({ message: 'Internal Server Error', error: error.message })
    };
  }
};
