import json
import boto3
import time
import os
from botocore.exceptions import ClientError

dynamodb = boto3.resource('dynamodb')
sns = boto3.client('sns')
s3 = boto3.client('s3')
sqs = boto3.client('sqs')
cloudwatch = boto3.client('cloudwatch')

def lambda_handler(event, context):
    table_name = os.environ['DEVICE_TOKENS_TABLE']
    analytics_bucket = os.environ['ANALYTICS_BUCKET']
    dlq_url = os.environ['DLQ_URL']
    max_retries = int(os.environ.get('MAX_RETRIES', '3'))
    initial_backoff = int(os.environ.get('INITIAL_BACKOFF', '1'))

    table = dynamodb.Table(table_name)

    for record in event.get('Records', [event]):
        message = json.loads(record.get('body', json.dumps(record)))
        user_id = message.get('userId')
        notification_content = message.get('content')
        platform = message.get('platform', 'all')

        if not user_id or not notification_content:
            print('Missing userId or content')
            continue

        # Query device tokens for the user
        try:
            if platform == 'all':
                response = table.query(
                    KeyConditionExpression='userId = :uid',
                    ExpressionAttributeValues={':uid': user_id}
                )
            else:
                response = table.query(
                    IndexName='PlatformIndex',
                    KeyConditionExpression='platform = :platform',
                    ExpressionAttributeValues={':platform': platform}
                )

            devices = response.get('Items', [])

            for device in devices:
                device_token = device.get('deviceToken')
                device_platform = device.get('platform')

                # Retry logic with exponential backoff
                for attempt in range(max_retries):
                    try:
                        # Send push notification via SNS
                        platform_arn = os.environ.get(f'{device_platform.upper()}_APPLICATION_ARN')
                        if platform_arn:
                            endpoint_response = sns.create_platform_endpoint(
                                PlatformApplicationArn=platform_arn,
                                Token=device_token
                            )

                            sns.publish(
                                TargetArn=endpoint_response['EndpointArn'],
                                Message=json.dumps({
                                    'default': notification_content['message'],
                                    'APNS_SANDBOX': json.dumps({
                                        'aps': {
                                            'alert': notification_content['message'],
                                            'sound': 'default',
                                            'badge': 1
                                        }
                                    }),
                                    'GCM': json.dumps({
                                        'notification': {
                                            'title': notification_content.get('title', 'Notification'),
                                            'body': notification_content['message']
                                        }
                                    })
                                }),
                                MessageStructure='json'
                            )

                        # Log success metrics
                        cloudwatch.put_metric_data(
                            Namespace='PushNotifications',
                            MetricData=[
                                {
                                    'MetricName': 'SuccessfulDelivery',
                                    'Value': 1,
                                    'Unit': 'Count'
                                }
                            ]
                        )

                        # Store analytics
                        s3.put_object(
                            Bucket=analytics_bucket,
                            Key=f"success/{user_id}/{context.request_id}.json",
                            Body=json.dumps({
                                'userId': user_id,
                                'deviceToken': device_token,
                                'platform': device_platform,
                                'timestamp': context.aws_request_id,
                                'status': 'success'
                            })
                        )

                        break  # Success, exit retry loop

                    except ClientError as e:
                        if attempt < max_retries - 1:
                            # Exponential backoff
                            wait_time = initial_backoff * (2 ** attempt)
                            print(f"Attempt {attempt + 1} failed, waiting {wait_time} seconds")
                            time.sleep(wait_time)
                        else:
                            # Final attempt failed, send to DLQ
                            print(f"Failed to deliver after {max_retries} attempts")

                            sqs.send_message(
                                QueueUrl=dlq_url,
                                MessageBody=json.dumps({
                                    'userId': user_id,
                                    'deviceToken': device_token,
                                    'platform': device_platform,
                                    'error': str(e),
                                    'content': notification_content
                                })
                            )

                            # Log failure metrics
                            cloudwatch.put_metric_data(
                                Namespace='PushNotifications',
                                MetricData=[
                                    {
                                        'MetricName': 'FailedDelivery',
                                        'Value': 1,
                                        'Unit': 'Count'
                                    }
                                ]
                            )

                            # Store failure analytics
                            s3.put_object(
                                Bucket=analytics_bucket,
                                Key=f"failures/{user_id}/{context.request_id}.json",
                                Body=json.dumps({
                                    'userId': user_id,
                                    'deviceToken': device_token,
                                    'platform': device_platform,
                                    'timestamp': context.aws_request_id,
                                    'status': 'failed',
                                    'error': str(e)
                                })
                            )

        except Exception as e:
            print(f"Error processing notification: {str(e)}")
            return {'statusCode': 500, 'body': json.dumps({'error': str(e)})}

    return {'statusCode': 200, 'body': json.dumps('Notifications processed')}
