#!/bin/bash
yum update -y
yum install -y httpd
yum install -y amazon-cloudwatch-agent

# Configure CloudWatch agent
cat > /opt/aws/amazon-cloudwatch-agent/etc/amazon-cloudwatch-agent.json << 'EOF'
{
  "metrics": {
    "namespace": "AWS/EC2",
    "metrics_collected": {
      "cpu": {
        "measurement": [
          "cpu_usage_idle",
          "cpu_usage_iowait",
          "cpu_usage_user",
          "cpu_usage_system"
        ],
        "metrics_collection_interval": 300
      },
      "disk": {
        "measurement": [
          "used_percent"
        ],
        "metrics_collection_interval": 300,
        "resources": [
          "*"
        ]
      },
      "mem": {
        "measurement": [
          "mem_used_percent"
        ],
        "metrics_collection_interval": 300
      }
    }
  },
  "logs": {
    "logs_collected": {
      "files": {
        "collect_list": [
          {
            "file_path": "/var/log/httpd/access_log",
            "log_group_name": "${ApplicationLogGroup}",
            "log_stream_name": "{instance_id}/httpd/access.log"
          },
          {
            "file_path": "/var/log/httpd/error_log",
            "log_group_name": "${ApplicationLogGroup}",
            "log_stream_name": "{instance_id}/httpd/error.log"
          }
        ]
      }
    }
  }
}
EOF

# Start CloudWatch agent
/opt/aws/amazon-cloudwatch-agent/bin/amazon-cloudwatch-agent-ctl \
  -a fetch-config -m ec2 -s \
  -c file:/opt/aws/amazon-cloudwatch-agent/etc/amazon-cloudwatch-agent.json

# Configure Apache
systemctl start httpd
systemctl enable httpd

# Create simple index page
cat > /var/www/html/index.html << 'EOF'
<!DOCTYPE html>
<html>
<head>
    <title>${EnvironmentName} - Secure Web Application</title>
    <style>
        body { font-family: Arial, sans-serif; margin: 40px; }
        .header { background-color: #232f3e; color: white; padding: 20px; }
        .content { padding: 20px; }
        .security-info { background-color: #e8f5e8; padding: 15px; margin: 20px 0; }
    </style>
</head>
<body>
    <div class="header">
        <h1>Secure Web Application - ${EnvironmentName}</h1>
    </div>
    <div class="content">
        <h2>Security Features Implemented:</h2>
        <div class="security-info">
            <ul>
                <li>EC2 instances in private subnets (no public IPs)</li>
                <li>HTTPS-only traffic via Application Load Balancer</li>
                <li>S3 bucket with AES-256 encryption</li>
                <li>AWS Shield DDoS protection</li>
                <li>IAM roles with least privilege access</li>
                <li>CloudTrail API logging enabled</li>
                <li>AWS Config continuous monitoring</li>
                <li>Multi-region backup strategy</li>
                <li>CloudWatch alarms for high CPU usage</li>
            </ul>
        </div>
        <p>Instance ID: $(curl -s http://169.254.169.254/latest/meta-data/instance-id)</p>
        <p>Availability Zone: $(curl -s http://169.254.169.254/latest/meta-data/placement/availability-zone)</p>
    </div>
</body>
</html>
EOF

# Health check endpoint
echo "OK" > /var/www/html/health.html
