import json
import boto3
import os

def lambda_handler(event, context):
    # Example of using KMS key from environment
    kms_key_id = os.environ.get('KMS_KEY_ID')

    return {
        'statusCode': 200,
        'body': json.dumps({
            'message': 'Hello from secure Lambda!',
            'kms_key': kms_key_id
        })
    }
