import boto3
import os
from datetime import datetime, timezone

def lambda_handler(event, context):
    iam = boto3.client('iam')
    sns = boto3.client('sns')

    sns_topic = os.environ['SNS_TOPIC_ARN']
    enforce = os.environ['ENFORCE_ROTATION'] == 'true'

    users = iam.list_users()['Users']
    old_keys = []

    for user in users:
        keys = iam.list_access_keys(UserName=user['UserName'])['AccessKeyMetadata']
        for key in keys:
            age = (datetime.now(timezone.utc) - key['CreateDate']).days
            if age > 90:
                old_keys.append({'User': user['UserName'], 'KeyId': key['AccessKeyId'], 'Age': age})
                if enforce:
                    iam.update_access_key(UserName=user['UserName'], AccessKeyId=key['AccessKeyId'], Status='Inactive')

    if old_keys:
        message = 'Access Keys requiring rotation:\n' + '\n'.join(
            [f"- User: {k['User']}, Key: {k['KeyId']}, Age: {k['Age']} days{' (DEACTIVATED)' if enforce else ''}" for k in old_keys]
        )
        sns.publish(TopicArn=sns_topic, Subject='Access Key Rotation Alert', Message=message)

    return {'statusCode': 200, 'body': f'Processed {len(old_keys)} old keys'}
