DashboardBody.Fn::Sub:
{
  "widgets": [
    {
      "type": "metric",
      "x": 0,
      "y": 0,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/EC2", "CPUUtilization", "AutoScalingGroupName", "${WebAppAutoScalingGroup}" ]
        ],
        "period": 300,
        "stat": "Average",
        "region": "${AWS::Region}",
        "title": "EC2 CPU Utilization"
      }
    },
    {
      "type": "metric",
      "x": 12,
      "y": 0,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/RDS", "CPUUtilization", "DBInstanceIdentifier", "${RDSInstance}" ]
        ],
        "period": 300,
        "stat": "Average",
        "region": "${AWS::Region}",
        "title": "RDS CPU Utilization"
      }
    },
    {
      "type": "metric",
      "x": 0,
      "y": 6,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/ApplicationELB", "RequestCount", "LoadBalancer", "${ApplicationLoadBalancer.LoadBalancerFullName}" ]
        ],
        "period": 300,
        "stat": "Sum",
        "region": "${AWS::Region}",
        "title": "ALB Request Count"
      }
    },
    {
      "type": "metric",
      "x": 12,
      "y": 6,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/RDS", "FreeStorageSpace", "DBInstanceIdentifier", "${RDSInstance}" ]
        ],
        "period": 300,
        "stat": "Average",
        "region": "${AWS::Region}",
        "title": "RDS Free Storage Space"
      }
    }
  ]
}
