DashboardBody.Fn::Sub:
{
  "widgets": [
    {
      "type": "metric",
      "x": 0,
      "y": 0,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/ECS", "CPUUtilization", "ClusterName", "${TapstackECSCluster}" ],
          [ ".", "MemoryUtilization", ".", "." ]
        ],
        "period": 300,
        "stat": "Average",
        "region": "${AWS::Region}",
        "title": "ECS Cluster Resource Utilization"
      }
    },
    {
      "type": "metric",
      "x": 0,
      "y": 6,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/ApplicationELB", "RequestCount", "LoadBalancer", "${TapstackALB}" ],
          [ ".", "TargetResponseTime", ".", "." ]
        ],
        "period": 300,
        "stat": "Sum",
        "region": "${AWS::Region}",
        "title": "Application Load Balancer Metrics"
      }
    },
    {
      "type": "metric",
      "x": 0,
      "y": 12,
      "width": 12,
      "height": 6,
      "properties": {
        "metrics": [
          [ "AWS/RDS", "DatabaseConnections", "DBInstanceIdentifier", "${TapstackDBInstance}" ],
          [ ".", "CPUUtilization", ".", "." ]
        ],
        "period": 300,
        "stat": "Average",
        "region": "${AWS::Region}",
        "title": "RDS Database Metrics"
      }
    }
  ]
}
