import json

def handler(event, context):
    report_id = event.get('reportId')
    report_data = event.get('content', {})
    validation_errors = []

    if not report_data.get("entity_name"):
        validation_errors.append("Missing required field: entity_name")

    min_transactions = 20
    if report_data.get("transaction_count", 0) < min_transactions:
        validation_errors.append(f"Transaction count ({report_data.get('transaction_count')}) below minimum threshold of {min_transactions}.")

    max_value = 500000.00
    if report_data.get("total_value", 0) > max_value:
        validation_errors.append(f"Total value ({report_data.get('total_value')}) exceeds maximum allowed value of {max_value}.")

    is_valid = len(validation_errors) == 0

    print(f"Validation for Report {report_id}: {'Success' if is_valid else 'Failure'}")
    if validation_errors:
        print(f"Validation errors: {validation_errors}")

    event['validationResult'] = {
        "isValid": is_valid,
        "errors": validation_errors
    }

    return event

