{
  "Comment": "Regulatory Report Generation and Delivery Workflow",
  "StartAt": "GenerateReport",
  "States": {
    "GenerateReport": {
      "Type": "Task",
      "Resource": "arn:aws:states:::lambda:invoke",
      "Parameters": {
        "FunctionName": "${GenerateReportLambdaArn}",
        "Payload.$": "$"
      },
      "TimeoutSeconds": 300,
      "Retry": [ { "ErrorEquals": ["Lambda.ServiceException", "Lambda.AWSAPICallFailed"], "IntervalSeconds": 2, "MaxAttempts": 3, "BackoffRate": 2.0 } ],
      "OutputPath": "$.Payload",
      "Next": "ValidateReport"
    },
    "ValidateReport": {
      "Type": "Task",
      "Resource": "arn:aws:states:::lambda:invoke",
      "Parameters": {
        "FunctionName": "${ValidateReportLambdaArn}",
        "Payload.$": "$"
      },
      "TimeoutSeconds": 300,
      "OutputPath": "$.Payload",
      "Next": "ValidationChoice"
    },
    "ValidationChoice": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.validationResult.isValid",
          "BooleanEquals": true,
          "Next": "DeliverReport"
        }
      ],
      "Default": "ValidationFailedNotification"
    },
    "DeliverReport": {
      "Type": "Task",
      "Resource": "arn:aws:states:::lambda:invoke",
      "Parameters": {
        "FunctionName": "${DeliverReportLambdaArn}",
        "Payload.$": "$"
      },
      "TimeoutSeconds": 300,
      "OutputPath": "$.Payload",
      "Catch": [ { "ErrorEquals": ["States.ALL"], "Next": "DeliveryFailedNotification", "ResultPath": "$.error" } ],
      "Next": "ReportGenerationSuccess"
    },
    "ValidationFailedNotification": {
      "Type": "Fail",
      "Cause": "Report failed validation rules",
      "Error": "ValidationFailed"
    },
    "DeliveryFailedNotification": {
      "Type": "Fail",
      "Cause": "Report delivery or S3 storage failed",
      "Error": "DeliveryFailed"
    },
    "ReportGenerationSuccess": {
      "Type": "Succeed"
    }
  }
}
