#!/bin/bash -xe
# Update system
yum update -y

# Install required packages
yum install -y httpd

# Configure and start Apache
systemctl start httpd
systemctl enable httpd

# Create web content with instance information
cat << 'EOF' > /var/www/html/index.html
<!DOCTYPE html>
<html>
<head>
    <title>${EnvironmentName} - Web Application</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            margin: 0;
            padding: 0;
            background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .container {
            background: white;
            padding: 40px;
            border-radius: 15px;
            box-shadow: 0 20px 60px rgba(0,0,0,0.3);
            max-width: 800px;
            width: 90%;
        }
        h1 {
            color: #333;
            border-bottom: 3px solid #667eea;
            padding-bottom: 15px;
            margin-bottom: 30px;
        }
        .info-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 20px;
            margin: 30px 0;
        }
        .info-box {
            padding: 15px;
            background: #f8f9fa;
            border-left: 4px solid #667eea;
            border-radius: 5px;
        }
        .info-label {
            font-weight: bold;
            color: #555;
            margin-bottom: 5px;
        }
        .info-value {
            color: #333;
            font-size: 14px;
            word-break: break-all;
        }
        .status {
            display: inline-block;
            padding: 5px 15px;
            background: #28a745;
            color: white;
            border-radius: 20px;
            font-size: 12px;
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>${EnvironmentName} Web Application - Instance 1</h1>
        <p>Welcome to the multi-tier web application running on LocalStack!</p>

        <div class="info-grid">
            <div class="info-box">
                <div class="info-label">S3 Bucket</div>
                <div class="info-value">${S3Bucket}</div>
            </div>
            <div class="info-box">
                <div class="info-label">Region</div>
                <div class="info-value">${AWS::Region}</div>
            </div>
            <div class="info-box">
                <div class="info-label">Instance Type</div>
                <div class="info-value">${InstanceType}</div>
            </div>
            <div class="info-box">
                <div class="info-label">Environment</div>
                <div class="info-value">${EnvironmentName}</div>
            </div>
        </div>

        <div class="status">Instance 1 Healthy</div>

        <p style="margin-top: 30px; color: #666; font-size: 14px;">
            This instance has read/write access to the S3 bucket and is part of the high-availability infrastructure.
        </p>
    </div>
</body>
</html>
EOF

