#!/bin/bash -xe
# Update system
yum update -y

# Install required packages
yum install -y httpd

# Configure and start Apache
systemctl start httpd
systemctl enable httpd

# Create web content
cat << 'EOF' > /var/www/html/index.html
<!DOCTYPE html>
<html>
<head>
    <title>${EnvironmentName} - Auto Scaled Instance</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            margin: 0;
            padding: 0;
            background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        .container {
            background: white;
            padding: 40px;
            border-radius: 15px;
            box-shadow: 0 20px 60px rgba(0,0,0,0.3);
            max-width: 800px;
            width: 90%;
        }
        h1 {
            color: #333;
            border-bottom: 3px solid #f5576c;
            padding-bottom: 15px;
            margin-bottom: 30px;
        }
        .badge {
            display: inline-block;
            padding: 5px 15px;
            background: #f5576c;
            color: white;
            border-radius: 20px;
            font-size: 12px;
            margin-left: 10px;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Auto Scaled Instance <span class="badge">ASG</span></h1>
        <p>This instance was automatically provisioned by the Auto Scaling Group on LocalStack.</p>

        <div style="background: #f8f9fa; padding: 20px; border-radius: 10px; margin: 20px 0;">
            <p><strong>S3 Bucket:</strong> ${S3Bucket}</p>
            <p><strong>Region:</strong> ${AWS::Region}</p>
            <p><strong>Environment:</strong> ${EnvironmentName}</p>
        </div>
    </div>
</body>
</html>
EOF
