const { DynamoDBClient, PutItemCommand, GetItemCommand } = require('@aws-sdk/client-dynamodb');
const { marshall, unmarshall } = require('@aws-sdk/util-dynamodb');
const ddbClient = new DynamoDBClient({ region: process.env.AWS_REGION });

exports.handler = async (event) => {
  console.log('Received event:', JSON.stringify(event, null, 2));
  const itemsTableName = process.env.ITEMS_TABLE_NAME;
  try {
    switch (event.httpMethod) {
      case 'POST':
        const item = JSON.parse(event.body);
        const putParams = {
          TableName: itemsTableName,
          Item: marshall(item),
        };
        await ddbClient.send(new PutItemCommand(putParams));
        return { statusCode: 201, body: JSON.stringify({ message: 'Item created successfully' }), };
      case 'GET':
        const itemId = event.queryStringParameters?.itemId;
        if (!itemId) {
          return { statusCode: 400, body: JSON.stringify({ message: 'Missing itemId query parameter' }), };
        }
        const getParams = {
          TableName: itemsTableName,
          Key: marshall({ itemId }),
        };
        const { Item } = await ddbClient.send(new GetItemCommand(getParams));
        if (!Item) {
          return { statusCode: 404, body: JSON.stringify({ message: 'Item not found' }), };
        }
        return { statusCode: 200, body: JSON.stringify(unmarshall(Item)), };
      default:
        return { statusCode: 405, body: JSON.stringify({ message: 'Method Not Allowed' }), };
    }
  } catch (error) {
    console.error('Error:', error);
    return { statusCode: 500, body: JSON.stringify({ message: 'Internal Server Error' }), };
  }
};

